import React from 'react';
import PageNavbar from './PageNavbar';
import GlobalCauseRow from './GlobalCauseRow';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../style/GlobalCauses.css';

export default class GlobalCauses extends React.Component {
	constructor(props) {
		super(props);

        this.state = {
			selectedCountry: "",
			selectedYear: "",
            countries: [],
            data: []
        };

        this.submitC = this.submit.bind(this);
		this.handleYearChange = this.handleYearChange.bind(this);
		this.handleCountryChange = this.handleCountryChange.bind(this);
	}

    componentDidMount() {
        fetch("http://localhost:8081/gccountries", 
        {
            method: 'GET'
        }).then(res => {
            return res.json();
        }, err => {
            console.log(err);
        }).then(countriesList => {
			if (!countriesList) return;

			console.log(typeof countriesList);
			console.log(countriesList);

            let countriesDivs = countriesList.map((country, i) => 
                <option key={i} value={country.country}>{country.country}</option>
            );

            this.setState({
                countries: countriesDivs,
                selectedCountry: countriesList[0].country
            });
        }, err => {
            console.log(err);
        });
        fetch("http://localhost:8081/gccauses", 
        {
            method: 'GET'
        }).then(res => {
            return res.json();
        }, err => {
            console.log(err);
        }).then(causesList => {
			if (!causesList) return;

            let causesDivs = causesList.map((cause, i) => 
                <option key={i} value={cause.cause}>{cause.cause}</option>
            );

            this.setState({
                causes: causesDivs,
                selectedCause1: causesList[0].cause,
                selectedCause2: causesList[0].cause,
            });
        }, err => {
            console.log(err);
        });
    }

	handleCountryChange(e) {
		this.setState({
			selectedYear: e.target.value

		});
	}

    submitCountry() {
		console.log('submit button pressed');
		fetch(`http://localhost:8081/coronavirus/'${this.state.selectedCountry}'`, {
			method: 'GET',
		}).then(res => {
			return res.json();
		}, err => {
			console.log(err);
		}).then(coronaDataList => {
			if (!coronaDataList) return;

			console.log(typeof coronaDataList);
			console.log(coronaDataList);

			let coronaDataDivs = coronaDataList.map((data, i) =>
                //<BestGenreRow key={i} genre={genre.genre} rating={genre.avg_rating}/>
                <GlobalCauseRow key={i} date={data.date_checked} confirmed={data.confirmed}
                    recovered={data.recovered} deaths={data.deaths} 
                    confirmed_globally={data.confirmed_glob}
                    recovered_globally={data.recovered_glob}
                    deaths_globally={data.deaths_glob}/>
			);

			this.setState({
				data: coronaDataDivs
			});
		}, err => {
			console.log(err);
		});
	}


	render() {

		return (
			<div className="CoronaVirus">
				<PageNavbar active="coronavirus" />

				<div className="container globalcauses-container">
			      <div className="jumbotron">
			        <div className="h5">Global Causes</div>

			        <div className="countries-container">
			          <div className="dropdown-container">
			            <select value={this.state.selectedCountry} onChange={this.handleChange} className="dropdown" id="countriesDropdown">
			            	{this.state.countries}
			            </select>
			            <button className="submit-btn" id="countriesSubmitBtn" onClick={this.submitCountry}>Submit</button>
			          </div>
			        </div>
			      </div>
			      <div className="jumbotron">
			        <div className="coronadata-container">
			          <div className="coronadata-header">
			            <div className="header"><strong>Year</strong></div>
			            <div className="header"><strong>Cause</strong></div>
                        <div className="header"><strong>Deaths Globally</strong></div>
			          </div>
			          <div className="results-container" id="results">
			            {this.state.data}
			          </div>
			        </div>
			      </div>
			    </div>
			</div>
		);
	}
}
